<template>  <view class="app">    <van-form @submit="onSubmit" class="form">      <van-cell-group inset>        <!-- 通过 pattern 进行正则校验 -->        <view class="label">手机号</view>        <van-field          type="number"          autocomplete="off"          v-model="form.mobile"          name="手机号"          label=""          placeholder="请输入您的手机号"          :rules="[{ required: true, message: '请填写手机号'}]"        />        <view class="label" style="margin-top: 55rpx">密码</view>        <van-field          class="input"          v-model="form.password"          autocomplete="new-password"          :readonly="readonlyVisible"          @click="changeReadonly"          ref="input"          type="password"          name="密码"          label=""          placeholder="请输入您的密码"          :rules="[{ required: true, message: '请填写密码'}]"        />      </van-cell-group>      <view style="margin: 105rpx 16rpx 0;">        <van-button class="button" block type="primary" native-type="submit">          登 录        </van-button>      </view>    </van-form>    <view class="register">      <van-button class="button button2" block type="primary" @click="goRegister">        注 册      </van-button>    </view>  </view></template><script>export default {  name: "login",  data() {    return {      readonlyVisible: true,      form: {        mobile: '',        password: '',      }    }  },  mounted() {  },  methods: {    changeReadonly() {      this.readonlyVisible = false      this.$nextTick(() => {        this.$refs.input.focus();      })    },    goRegister() {      console.log('去注册')    },    onSubmit() {      // 本地缓存账号密码      // if(this.form.savePassword){      //   uni.setStorageSync('cmms-login-savepassword',this.form)      // }      uni.setStorageSync('cookie', 1516545684165468); //保存Cookie到Storage      uni.reLaunch({        url: '/pages/base/index/index'      })      // uni.showToast({      //   title: '登陆成功',      //   duration: 2000      // });    }  }}</script><style scoped lang="scss">  .app {    background-image: url('../../../static/resources/login/login.png');    background-size: 100%;    background-repeat: no-repeat;    min-height: 100vh;    overflow: hidden;    background-color: #fff;  }  .form {    height: 100%;    padding-top: 688rpx;    margin: 0 56rpx auto;    .label {      font-size: 30rpx;      height: 30rpx;      line-height: 30rpx;      color: #333333;      margin-bottom: 10rpx;    }    .input {      border-bottom: 2px solid #e9e9e9!important;    }  }  .button {    background: #00479d;    height: 100rpx;    line-height: 100rpx;    font-size: 30rpx;    border-radius: 10rpx;  }  .button2 {    background: #fff;    color: #00a1e9;    border: 1rpx solid #00a1e9;  }  .register {    padding: 0 72rpx;    margin-top: 52rpx;  }  /deep/ .form .van-cell {    height: 84rpx;    padding: 25rpx 1rpx;    border-bottom: 1px solid #e9e9e9;  }  /deep/ .van-field__control {    font-size: 26rpx;  }  /deep/ .van-field__error-message {    display: none;  }  /deep/ input:-internal-autofill-selected {    background: #ffffff!important;  }</style>