<template>  <view class="body-tabbar home">    <ExceptionHeader title="异常处理" @goReporting="goReporting"></ExceptionHeader>    <Tabbar active="index"></Tabbar>    <view class="exception-header">      <view class="exception-value">        <view class="num">          1085        </view>        <view class="title">          异常总数        </view>      </view>      <view class="exception-value">        <view class="num">          1085        </view>        <view class="title">          已处理        </view>      </view>      <view class="exception-value">        <view class="num">          1085%        </view>        <view class="title">          处理比率        </view>      </view>    </view>    <view class="exception-kind">      <view class="btn" :class="!exceptionKind?'active':null" @click="cutExceptionData">        未处理异常      </view>      <view class="btn" :class="exceptionKind?'active':null" @click="cutExceptionData">        已处理异常      </view>    </view>    <view class="exception-container">      <view class="exception-detail" v-for="(item,index) in 8" :key="index">        <view class="name">储能室并网异常</view>        <view class="info">          <view class="info-content">            泰国政府监管放射性物质和核研究的机构“和平利用原子能办公室”多名官员19日在巴真府甲民武里一座冶炼厂的熔炉探测到铯137。这座冶炼厂距离丢失含铯137部件的火力发电厂大约10公里          </view>          <view class="info-img">            <image src="../../../static/resources/operate/exception-handling.jpg"></image>          </view>        </view>        <view class="operate">          <view class="operate-time">2023-03-22</view>          <view class="operate-box">            <view class="button" @click="exceptionConfirm(item)">异常确认</view>            <view class="button" @click="exceptionSolve(item)">异常处理</view>          </view>        </view>      </view>    </view>    <!-- 异常上报 --><!--    <view class="test" @click="exceptionReporting">--><!--      <image src="../../../static/resources/operate/exception-reporting.png"></image>--><!--    </view>-->  </view></template><script>export default {  name: "exceptionHandling",  data() {    return {      exceptionKind: 0, // 默认未处理异常    }  },  mounted() {    this.getExceptionData()  },  methods: {    /*异常上报*/    goReporting() {      console.log('异常上报')    },    getExceptionData() {      if (this.exceptionKind) {        console.log('已处理异常')      } else {        console.log('未处理异常')      }    },    cutExceptionData() {      this.exceptionKind = this.exceptionKind ? 0 : 1      this.getExceptionData()    },    /*异常确认*/    exceptionConfirm(item) {      console.log('异常确认')    },    /*异常处理*/    exceptionSolve(item) {      console.log('异常处理')    }  }}</script><style scoped lang="scss">  .test {    width: 142rpx;    height: 28rpx;    position: absolute;    z-index: 999999;    color: #ffffff;    font-size: 24rpx;    top: 44rpx;    right: 30rpx;    transform: translateY(-50%);    image {      width: 100%;      height: 100%;    }  }  .home {    background-image: url('../../../static/resources/operate/exception-handling.jpg');    background-position: 0 88rpx;    background-size: 100%;    //min-height: 100vh;    background-repeat: no-repeat;  }  .exception-header {    display: flex;    height: 211rpx;    padding-top: 45rpx;    box-sizing: border-box;    .exception-value {      width: 33%;      text-align: center;      .num {        font-size: 62rpx;        line-height: 62rpx;        color: #f9cc2e;        margin-bottom: 22rpx;        font-family: fantasy;        letter-spacing: 3rpx;      }      .title {        font-size: 26rpx;        line-height: 26rpx;        color: #fff;        letter-spacing: 3rpx;      }    }  }  .exception-kind {    display: flex;    height: 90rpx;    line-height: 90rpx;    font-size: 30rpx;    text-align: center;    margin-top: 45rpx;    .btn {      width: 50%;      color: #999999;      background: #ececec;    }    .btn.active {      color: #ffffff;      background: #00479d;    }  }  .exception-kind .btn:nth-child(1) {    border-radius: 12rpx 0 0 0;  }  .exception-kind .btn:nth-child(2) {    border-radius: 0 12rpx 0 0;  }  .exception-container {    max-height: 1000rpx;    overflow: hidden;    overflow-y: scroll;    .exception-detail {      box-sizing: border-box;      margin-top: 20rpx;      height: 360rpx;      border-radius: 12rpx;      background: #ffffff;      padding: 40rpx 30rpx;      .name {        font-size: 28rpx;        color: #333333;        font-weight: bold;        line-height: 28rpx;      }      .info {        display: flex;        justify-content: space-between;        height: 120rpx;        margin: 26rpx 0 46rpx 0;        .info-content {          width: 400rpx;          height: 100%;          color: #999999;          font-size: 24rpx;          overflow:hidden;          letter-spacing: 4rpx;          line-height: 40rpx;          text-overflow:ellipsis;          display:-webkit-box;    //将对象作为弹性伸缩盒子模型显示。          -webkit-box-orient:vertical;  // 从上到下垂直排列子元素          -webkit-line-clamp:3; //显示的行数        }        .info-img {          width: 200rpx;          height: 120rpx;          border-radius: 12rpx;          overflow: hidden;          image {            width: 100%;            height: 100%;          }        }      }      .operate {        display: flex;        justify-content: space-between;        view {          height: 65rpx;          line-height: 65rpx;        }        .operate-time {          font-size: 20rpx;          color: #999999;        }        .operate-box {          display: flex;          .button {            width: 170rpx;            line-height: 65rpx;            text-align: center;            font-size: 24rpx;            border-radius: 12rpx;            background: #00479d;            color: #ffffff;          }        }        .operate-box .button:last-child {          background: #ffffff;          color: #00479d;          border: 1rpx solid #00479d;          margin-left: 26rpx;        }      }    }  }</style>