<template>  <view class="body-tabbar">    <CommHeader title="远程控制"></CommHeader>    <Tabbar active="index"></Tabbar>    <view class="control">      <view class="header">        <view class="control-kind">          <view @click="cutControlKind">            <image src="../../../static/resources/operate/control1.png"></image>          </view>          <view @click="cutControlKind">            <image src="../../../static/resources/operate/control2.png"></image>          </view>          <view @click="cutControlKind">            <image src="../../../static/resources/operate/control3.png"></image>          </view>        </view>        <view class="control-switch">          <view class="select" @click="choose">            <view class="value">{{ getPickerText(select, 'columns') || '请选择分类' }}</view>            <i class="iconfont">&#xe65c;</i>          </view>          <view class="select" @click="choose">            <view class="value">请选择分类</view>            <i class="iconfont">&#xe65c;</i>          </view>          <view class="button">            <view class="btn" @click="allSwitchChange(1)">              全开            </view>            <view class="btn" @click="allSwitchChange(0)">              全关            </view>          </view>        </view>      </view>      <view class="container">        <view class="container-header">          <view class="index">            序号          </view>          <view class="address">            位置          </view>          <view class="operate">            操作          </view>        </view>        <view class="container-header container-content"              v-for="(item,index) in controlData" :key="index"              :class="index%2?'active':null">          <view class="index">            {{ index + 1 }}          </view>          <view class="address">            {{ item.address }}          </view>          <view class="operate" @click="switchChange(item,index)">            <image v-if="item.status" src="../../../static/resources/operate/open.png"></image>            <image v-else src="../../../static/resources/operate/close.png"></image>          </view>        </view>      </view>    </view>    <van-popup v-model="showPicker" round position="bottom">      <van-picker        :item-height="88"        :visible-item-count="6"        show-toolbar        :columns="columns"        @cancel="showPicker = false"        @confirm="onConfirm"      />    </van-popup>  </view></template><script>export default {  name: "remoteControl",  data() {    return {      showPicker: false,      columns: [        { text: '杭州你啊啊啊啊啊啊啊', value: 'Hangzhou' },        { text: '山东省烟台市莱山', value: 'Ningbo' },        { text: '温州', value: 'Wenzhou' },        { text: '绍兴', value: 'Shaoxing' },        { text: '湖州', value: 'Huzhou' },      ],      controlData: [        {          address: '你低洼地阿虎地',          status: 1        },        {          address: '你低洼地阿虎地',          status: 0        },        {          address: '你低洼地阿虎地',          status: 0        },        {          address: '你低洼地阿虎地',          status: 1        },      ],      select: ''    }  },  methods: {    getPickerText(value,arr) {      let str = value ? this[arr].filter(item=>item.value===value)[0].text : ''      return str    },    choose() {      this.showPicker = true    },    onConfirm(value, index) {      // console.log(value, index)      this.select = value.value      this.showPicker = false    },    switchChange(item,index) {      item.status = item.status ? 0 : 1      // this.controlData[index].status = item.status===1 ? 0 : 1    },    allSwitchChange(type) {      this.controlData = this.controlData.map(item=>{        item.status = type        return item      })    },    /*切换控制种类*/    cutControlKind() {    }  }}</script><style scoped lang="scss">  .control {    min-height: 100vh;    background:repeating-linear-gradient(to top, rgba(255,255,255,1), rgba(255,255,255,.2));    .header {      width: 690rpx;      //height: 500rpx;      //background: #f5f5f5;      //position: fixed;      top: 88rpx;      left: 30rpx;      padding: 0 35rpx;      box-sizing: border-box;      .control-kind {        height: 200rpx;        width: 100%;        display: flex;        view {          width: 33.3%;          //position: relative;          image {            width: 100rpx;            height: 100rpx;            display: block;            margin: 50rpx auto;          }        }      }      .control-switch {        height: 48rpx;        display: flex;        justify-content: space-between;        .select {          width: 182rpx;          display: flex;          font-size: 24rpx;          height: 100%;          line-height: 48rpx;          padding-right: 18rpx;          box-sizing: border-box;          justify-content: space-between;          .value {            overflow: hidden;            text-overflow: ellipsis;            white-space: nowrap;            color: #999999;          }          .iconfont {            color: #333333;            font-size: 28rpx;          }        }        .button {          display: flex;          justify-content: space-between;          .btn {            width: 90rpx;            line-height: 48rpx;            text-align: center;            color: #ffffff;            font-size: 20rpx;            background: #005ccc;            border-radius: 20rpx;          }        }        .button .btn:last-child{          margin-left: 10rpx;          background: #bebebe;        }      }    }    .container {      padding: 55rpx 35rpx 0;      .container-header {        height: 70rpx;        line-height: 70rpx;        color: #ffffff;        background: #00479d;        display: flex;        text-align: center;        font-size: 24rpx;        border-radius: 12rpx 12rpx 0 0;        view {          padding: 0 15rpx;        }        .index {          width: 16%;        }        .address {          width: 49%;        }        .operate {          width: 35%;          position: relative;          image {            width: 100rpx;            height: 48rpx;            position: absolute;            top: 50%;            left: 50%;            transform: translate(-50%,-50%);          }        }      }      .container-content {        height: 100rpx;        line-height: 100rpx;        color: #5c5c5c;        background: #ffffff;        border-radius: 0;        .address {          overflow:hidden; //超出的文本隐藏          text-overflow:ellipsis; //用省略号显示          white-space:nowrap; //不        }      }      .container-content.active {        background: #f4f4f4;      }    }  }  /deep/ .van-popup--bottom.van-popup--round {    z-index: 1000000!important;    height: 45%;  }  /deep/ .van-ellipsis {    font-size: 24rpx;  }  /deep/ .van-picker__toolbar {    height: 88rpx;  }  /deep/ .van-picker__cancel {    font-size: 24rpx!important;  }  /deep/ .van-picker__confirm {    font-size: 24rpx!important;  }</style>