<template>  <view class="body-tabbar home">    <CommHeader title="预警分析"></CommHeader>    <Tabbar active="index"></Tabbar>    <view class="warning-kind">      <view class="kind-name">        <view class="img-box">          <image src="../../../static/resources/operate/bar.png"></image>        </view>        <view class="chinese">用水分析</view>        <view class="english">warning kind</view>      </view>      <view class="echarts-box" id="waterUseAnalysis"></view>    </view>    <view class="warning-kind">      <view class="kind-name">        <view class="img-box">          <image src="../../../static/resources/operate/bar.png"></image>        </view>        <view class="chinese">用电分析</view>        <view class="english">warning kind</view>      </view>      <view class="echarts-box" id="electricityAnalysis"></view>    </view>    <view class="warning-kind">      <view class="kind-name">        <view class="img-box">          <image src="../../../static/resources/operate/bar.png"></image>        </view>        <view class="chinese">用气分析</view>        <view class="english">warning kind</view>      </view>      <view class="echarts-box" id="gasAnalysis"></view>    </view>  </view></template><script>import * as echarts from '@/static/comm/echarts.min.js';export default {  name: "warningAnalysis",  data() {    return {    }  },  mounted() {    this.renderChart()  },  methods: {    // 初始值    renderChart() {      this.waterUseChart = echarts.init(document.getElementById('waterUseAnalysis'))      let waterUseOption = {        grid: {          left: 55,          top: 52,          right: 35,          bottom: 40        },        backgroundColor: '#fff',        legend: {          show: true,          right: 40,          top: 10,          data: ['月用水量'],          textStyle: {            color: '#222222',            fontSize: 20          }        },        xAxis: {          data: ['1月', '2月', '3月', '4月',],          axisLine: {            lineStyle: {              color: '#3d5269'            }          },          axisTick: {            show: false          },          axisLabel: {            color: '#333333',            fontSize: 14          }        },        yAxis: {          name: "单位:吨",          nameTextStyle: {            color: '#000000',            fontSize: 20          },          nameGap: 25,          axisLine: {            show: true,            lineStyle: {              color: '#7f7f7f'            }          },          axisLabel: {            color: '#111111',            fontSize: 20          },          splitLine: {            show:true,            lineStyle: {              color: '#e6e6e6',              type: 'dashed'            }          },          // interval:500,        },        series: [{          name: '月用水量',          type: 'bar',          barWidth: 30,          itemStyle:{            normal:{              color:new echarts.graphic.LinearGradient(0, 0, 0, 1, [{                offset: 0,                color: '#5ef3ff'              }, {                offset: 1,                color: '#06a4f4'              }], false)            }          },          label: {          },          data: [25, 32,            {              value: 25,              itemStyle: {                color:new echarts.graphic.LinearGradient(0, 0, 0, 1, [{                  offset: 0,                  color: '#ffec4f'                }, {                  offset: 1,                  color: '#ff7300'                }], false)              },            }, 24, 47, 20, 12]        }]      };      this.waterUseChart.setOption(waterUseOption);      this.electricityChart = echarts.init(document.getElementById('electricityAnalysis'))      let electricityOption = {        grid: {          left: 55,          top: 52,          right: 35,          bottom: 40        },        backgroundColor: '#fff',        legend: {          show: true,          right: 40,          top: 10,          data: ['月用电量'],          textStyle: {            color: '#222222',            fontSize: 20          }        },        xAxis: {          data: ['1月', '2月', '3月', '4月',],          axisLine: {            lineStyle: {              color: '#3d5269'            }          },          axisTick: {            show: false          },          axisLabel: {            color: '#333333',            fontSize: 14          }        },        yAxis: {          name: "单位:kWh",          nameTextStyle: {            color: '#000000',            fontSize: 20          },          nameGap: 25,          axisLine: {            show: true,            lineStyle: {              color: '#7f7f7f'            }          },          axisLabel: {            color: '#111111',            fontSize: 20          },          splitLine: {            show:true,            lineStyle: {              color: '#e6e6e6',              type: 'dashed'            }          },          // interval:500,        },        series: [{          name: '月用电量',          type: 'bar',          barWidth: 30,          itemStyle:{            normal:{              color:new echarts.graphic.LinearGradient(0, 0, 0, 1, [{                offset: 0,                color: '#4ffffd'              }, {                offset: 1,                color: '#03c5c3'              }], false)            }          },          label: {          },          data: [            {              value: 25,              itemStyle: {                color:new echarts.graphic.LinearGradient(0, 0, 0, 1, [{                  offset: 0,                  color: '#ffec4f'                }, {                  offset: 1,                  color: '#ff7300'                }], false)              },            }, 32, 16, 24, 47, 20, 12]        }]      };      this.electricityChart.setOption(electricityOption);      this.gasChart = echarts.init(document.getElementById('gasAnalysis'))      let gasOption = {        grid: {          left: 55,          top: 52,          right: 35,          bottom: 40        },        backgroundColor: '#fff',        legend: {          show: true,          right: 40,          top: 10,          data: ['月用气量'],          textStyle: {            color: '#222222',            fontSize: 20          }        },        xAxis: {          data: ['1月', '2月', '3月', '4月',],          axisLine: {            lineStyle: {              color: '#3d5269'            }          },          axisTick: {            show: false          },          axisLabel: {            color: '#333333',            fontSize: 14          }        },        yAxis: {          name: "单位:M³",          nameTextStyle: {            color: '#000000',            fontSize: 20          },          nameGap: 25,          axisLine: {            show: true,            lineStyle: {              color: '#7f7f7f'            }          },          axisLabel: {            color: '#111111',            fontSize: 20          },          splitLine: {            show:true,            lineStyle: {              color: '#e6e6e6',              type: 'dashed'            }          },          // interval:500,        },        series: [{          name: '月用气量',          type: 'line',          smooth: true,          symbol: 'circle',//拐点设置为实心          symbolSize: 12,//拐点大小          barWidth: 30,          itemStyle:{            normal:{              color: '#0cebff',              // color:new echarts.graphic.LinearGradient(0, 0, 0, 1, [{              //   offset: 0,              //   color: '#4ffffd'              // }, {              //   offset: 1,              //   color: '#03c5c3'              // }], false)            }          },          lineStyle: {            color: '#3ecfff'          },          areaStyle: {            color: '#d3f3fe',          },          label: {          },          data: [25, 32, 16, 24,]        }]      };      this.gasChart.setOption(gasOption);      // 这里是用于窗口变化时的自适应，利用的是echarts自带的resize方法      window.addEventListener('resize', () => {        this.waterUseChart.resize()        this.electricityChart.resize()        this.gasChart.resize()      });    },    // 监听配置数据变化，并重新渲染    updateEcharts(newValue, oldValue, ownerInstance, instance) {      if (this.waterUseChart) {        this.waterUseChart.setOption(newValue);      }      if (this.electricityChart) {        this.electricityChart.setOption(newValue);      }      if (this.gasChart) {        this.gasChart.setOption(newValue);      }    },  }}</script><style scoped lang="scss">  .home {    min-height: 100vh;    overflow: hidden;    background-color: #f5f5f5;    .warning-kind {      box-sizing: border-box;      height: 520rpx;      margin-top: 30rpx;      background: #ffffff;      border-radius: 10rpx;      padding: 45rpx 33rpx;      .kind-name {        display: flex;        view {          line-height: 28rpx;        }        .img-box {          width: 8rpx;          height: 28rpx;          image {            width: 100%;            height: 100%;          }        }        .chinese {          color: #00479d;          font-size: 28rpx;          font-weight: bold;          margin: 0 14rpx 0 17rpx;        }        .english {          color: #a6bfdd;          font-size: 19rpx;          line-height: 31rpx;        }      }      .echarts-box {        height: 372rpx;        margin-top: 27rpx;        //background: red;      }    }  }</style>